select num_edges as 'number of edges', replace(cast(primary_roadnetwork_expansion_time + collisions_computation_time + primitives_extraction_time + secondary_roadnetwork_expansion_time as varchar), '.', ',') as 'millis.' from statistics where config_name like '%ld%' order by num_edges;
select num_edges as 'number of edges', replace(cast((primary_roadnetwork_expansion_time + collisions_computation_time + primitives_extraction_time + secondary_roadnetwork_expansion_time) / num_edges as varchar), '.', ',') as 'millis. per edge' from statistics where config_name like '%ld%' order by num_edges;
select num_edges as 'number of edges', replace(cast(primary_roadnetwork_expansion_time as varchar), '.', ',') as 'primary roadnetwork extension time (ms)' from statistics where config_name like '%ld%' order by num_edges;
select num_edges as 'number of edges', replace(cast(collisions_computation_time as varchar), '.', ',') as 'collisions computation time (ms)' from statistics where config_name like '%ld%' order by num_edges;
select num_edges as 'number of edges', replace(cast(primitives_extraction_time as varchar), '.', ',') as 'primitives extraction time (ms)' from statistics where config_name like '%ld%' order by num_edges;
select num_edges as 'number of edges', replace(cast(secondary_roadnetwork_expansion_time as varchar), '.', ',') as 'secondary roadnetwork expansion time (ms)' from statistics where config_name like '%ld%' order by num_edges;
select num_edges as 'number of edges', replace(cast(round(cast(memory_in_use as float) / 1048576.0, 3) as varchar), '.', ',') as 'memory in use (mb)' from statistics where config_name like '%ld%' order by num_edges;
select cast(substr(config_name, 17, 1) as int) as 'quadtree depth', replace(cast((primary_roadnetwork_expansion_time + collisions_computation_time + primitives_extraction_time + secondary_roadnetwork_expansion_time) / num_edges as varchar), '.', ',') as 'millis. per edge' from statistics where config_name like 'botafogo_bay_sd%';
select cast(substr(config_name, 17, 1) as int) as 'quadtree depth', num_collisions as 'no. of collision checks' from statistics where config_name like 'botafogo_bay_sd%';
select cast(substr(config_name, 17, 1) as int) as 'quadtree depth', replace(cast(collisions_computation_time as varchar), '.', ',') as 'collisions computation time (ms)' from statistics where config_name like 'botafogo_bay_sd%';
select cast(substr(config_name, 17, 1) as int) as 'quadtree depth', replace(cast(secondary_roadnetwork_expansion_time as varchar), '.', ',') as 'secondary roadnetwork expansion time (ms)' from statistics where config_name like 'botafogo_bay_sd%';
select cast(substr(config_name, 24, 1) as int) as 'quadtree depth', replace(cast((primary_roadnetwork_expansion_time + collisions_computation_time + primitives_extraction_time + secondary_roadnetwork_expansion_time) / num_edges as varchar), '.', ',') as 'millis. per edge' from statistics where config_name like 'rio_de_janeiro_city_sd%';
select cast(substr(config_name, 24, 1) as int) as 'quadtree depth', num_collisions as 'no. of collision checks'  from statistics where config_name like 'rio_de_janeiro_city_sd%';
select cast(substr(config_name, 24, 1) as int) as 'quadtree depth', replace(cast(collisions_computation_time as varchar), '.', ',') as 'collisions computation time (ms)' from statistics where config_name like 'rio_de_janeiro_city_sd%';
select cast(substr(config_name, 24, 1) as int) as 'quadtree depth', replace(cast(secondary_roadnetwork_expansion_time as varchar), '.', ',') as 'secondary roadnetwork expansion time (ms)' from statistics where config_name like 'rio_de_janeiro_city_sd%';
select expansion_kernel_blocks as 'number of blocks', replace(cast(avg(primary_roadnetwork_expansion_time + collisions_computation_time + primitives_extraction_time + secondary_roadnetwork_expansion_time) / num_edges as varchar), '.', ',') as 'millis. per edge' from statistics where config_name like '%pr%' group by 1;
select (expansion_kernel_blocks * expansion_kernel_threads) as 'number of threads', replace(cast(avg(primary_roadnetwork_expansion_time + collisions_computation_time + primitives_extraction_time + secondary_roadnetwork_expansion_time) / num_edges as varchar), '.', ',') as 'millis. per edge' from statistics where config_name like '%pr%' group by 1;
